import Media from "react-media";

import React from "react";
// console.log("props" + this.props.log);
// import Login from '../Login';
// import { useEffect } from 'react';
import { useLocation } from "react-router-dom";
// import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Card from "@mui/material/Card";
import Typography from "@mui/material/Typography";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";

const Welcome = (props) => {
  // console.log(this.props.location.names);

  //  console.log();

  const location = useLocation(props);
  let data = JSON.parse(localStorage.getItem("all_users1"));

  const curr_data = data.find(
    ({ username }) => username === location.state.name
  );

  console.log("use location => /*/*/ ", curr_data);

  return (
    <div>
      <div>
        <h1>Welcome {location.state.name}</h1>

        <Media
          queries={{
            medium: "(min-width: 600px) and (max-width: 1199px)",
            large: "(min-width: 1200px)",
          }}
        >
          {(matches) => (
            <React.Fragment>
              {matches.medium && (
                <p>
                  <div
                    className="App"
                    style={{
                      display: "flex",
                      flexWrap: "wrap",
                      justifyContent: "center",
                    }}
                  >
                    <Card
                      variant="outlined"
                      maxWidth="sm"
                      sx={{
                        p: 1,
                        pt: 2,
                        bgcolor: "info.main",
                        display: "flex",
                        boxShadow: 24,
                        flexWrap: "wrap",
                        justifyContent: "center",
                        maxWidth: 400,
                        borderRadius: 5,

                        m: 1,
                      }}
                    >
                      <Box component="span" sx={{ p: 0, bgcolor: "info.main" }}>
                        <Box
                          sx={{
                            display: "flex",
                            justifyContent: "center",
                            p: 1,
                            m: 1,

                            borderRadius: 1,
                          }}
                        >
                          <Typography
                            variant="h5"
                            component="legend"
                            sx={{ color: "white" }}
                          >
                            Registration Form
                          </Typography>
                        </Box>
                        <Box
                          sx={{
                            display: "flex",
                            justifyContent: "center",
                            p: 1,
                            gap: 1,
                            m: 1,
                            pb: 0,
                            mb: 0,
                            borderRadius: 1,
                          }}
                        >
                          <TextField
                            sx={{
                              borderRadius: 1,

                              boxShadow: 14,
                              bgcolor: "white",
                            }}
                            id="filled-basic"
                            label="First Name"
                            name="firstname"
                            fullWidth
                            margin="dense"
                            variant="filled"
                            value={curr_data.firstname}
                            // onChange={handleChange}
                          />

                          <TextField
                            sx={{
                              borderRadius: 1,

                              boxShadow: 14,
                              bgcolor: "white",
                            }}
                            id="filled-basic"
                            label="Last Name"
                            name="lastname"
                            fullWidth
                            margin="dense"
                            variant="filled"
                            value={curr_data.lastname}
                            // onChange={handleChange}
                          />
                        </Box>

                        <Box
                          sx={{
                            display: "flex",
                            justifyContent: "center",
                            flexDirection: "column",

                            p: 1,
                            m: 1,
                            mb: 0,
                            mt: 0,
                            pt: 0,
                            borderRadius: 1,
                          }}
                        >
                          {/* <span>{formErrors.firstname}</span> */}

                          <TextField
                            sx={{
                              borderRadius: 1,
                              boxShadow: 14,
                              bgcolor: "white",
                            }}
                            id="filled-basic"
                            label="User Name"
                            name="username"
                            fullWidth
                            margin="dense"
                            variant="filled"
                            value={curr_data.username}
                            // onChange={handleChange}
                          />
                          {/* <span>{formErrors.username}</span> */}

                          <TextField
                            sx={{
                              bgcolor: "white",
                              boxShadow: 14,
                              borderRadius: 1,
                              color: "text.primary",
                            }}
                            fullWidth
                            id="password-input"
                            label="Password"
                            name="password"
                            margin="dense"
                            variant="filled"
                            type="password"
                            autoComplete="current-password"
                            value={curr_data.password}
                            // onChange={handleChange}
                          />
                          {/* <span>{formErrors.password}</span> */}

                          <TextField
                            sx={{
                              borderRadius: 1,
                              boxShadow: 14,
                              bgcolor: "white",
                            }}
                            id="outlined-basic"
                            label="e-Mail ID"
                            name="email"
                            fullWidth
                            margin="dense"
                            variant="filled"
                            value={curr_data.email}
                            // onChange={handleChange}
                          />
                          {/* <span>{formErrors.email}</span> */}

                          <TextField
                            sx={{
                              borderRadius: 1,
                              boxShadow: 14,
                              bgcolor: "white",
                            }}
                            id="outlined-basic"
                            label="Address"
                            name="address"
                            fullWidth
                            margin="dense"
                            variant="filled"
                            value={curr_data.address}
                            // onChange={handleChange}
                          />
                          {/* <span>{formErrors.address}</span> */}
                        </Box>
                        <Box
                          sx={{
                            display: "flex",
                            justifyContent: "center",
                            p: 1,
                            gap: 1,
                            m: 1,
                            pt: 0,
                            mt: 0,
                            pb: 0,
                            mb: 0,
                            borderRadius: 1,
                          }}
                        >
                          <FormControl
                            variant="filled"
                            value={curr_data.state}
                            // onChange={handleChange}
                            sx={{
                              minWidth: 120,
                              bgcolor: "white",
                              borderRadius: 1,
                              p: 0,
                              m: 1,
                              ml: 0,
                              boxShadow: 14,
                              mt: 0,
                              mb: 2,
                            }}
                          >
                            <InputLabel id="demo-simple-select-standard-label">
                              State
                            </InputLabel>
                            <Select
                              labelId="demo-simple-select-standard-label"
                              id="demo-simple-select-standard"
                              value={curr_data.state}
                              //   onChange={handleChange}
                              label="Age"
                              name="state"
                            >
                              <MenuItem value="curr_data.pncode">
                                <em>None</em>
                              </MenuItem>
                            </Select>
                          </FormControl>

                          <TextField
                            sx={{
                              borderRadius: 1,
                              boxShadow: 14,
                              bgcolor: "white",
                              m: 1,
                              mt: 0,
                              mb: 2,
                            }}
                            id="basic"
                            label="Pincode"
                            name="pincode"
                            type="number"
                            fullWidth
                            margin="dense"
                            variant="filled"
                            value={curr_data.pincode}
                            // onChange={handleChange}
                          />
                        </Box>

                        <Typography
                          variant="h7"
                          component="legend"
                          sx={{ color: "black", textAlign: "right" }}
                        >
                          {/* {formErrors.pincode} */}
                        </Typography>
                        {/* <span >{formErrors.pincode}</span> */}

                        {/* //Register Button */}
                        <Box
                          sx={{
                            display: "flex",
                            justifyContent: "center",
                            p: 1,
                            m: 1,

                            borderRadius: 1,
                          }}
                        ></Box>
                      </Box>
                    </Card>
                  </div>
                </p>
              )}

              {matches.large && <p>I am large!</p>}
            </React.Fragment>
          )}
        </Media>
      </div>

      {/* <p>{props.username.location.state.names} </p> */}
    </div>
  );
};

export default Welcome;

