    const darkTheme = useContext(ThemeContext);

    const darkStyle = {

            backgroundColor: darkTheme ? "#333" : "#ccc",
            color: !darkTheme ? "#333" : "#ccc",
            padding: "2em",
            margin: "2rem",

    }

    return (
        <div style={darkStyle}>
            Functional Component
            </div>

    )
}


export default FunctionalContextComponent
